    switch (action.type) {
      // push term in array todolist
      case 'ADDTASK':
        return [...todolist , action.payload]
      case 'DELETETASK' :
        todolist.splice(action.payload,1);
        return [...todolist]
      case 'EDITTASK':
         todo
      default:
        return todolist
    }
  }

