import React from 'react';

class TodoList extends React.Component {
   constructor(props){
       super(props)
       this.state = {input: ''}
   }

   getInput(event){
        this.setState({input:event.target})
   }


    render(){
        return (
            <div>
               <input  type='text' onChange={this.getInput}/>
                <button >Add</button>


        </div>
        ) ;   
    }
}
export default TodoList;
